#include <stdio.h>
#include <stdlib.h>
#include <errno.h>
#include "baseconv.h"

int main(void) {
char *str;
	str = base_convert("", "0123456789ABCDEF", "0123456789");
	if (str) {
		puts(str);
		free(str);
	}
	else {
		perror("base_convert");
		return EXIT_FAILURE;
	}
	str = base_convert("FFFFFFFF", "0123456789ABCDEF", "0123456789");
	if (str) {
		puts(str);
		free(str);
	}
	else {
		perror("base_convert");
		return EXIT_FAILURE;
	}
	str = base_convert("FFFFFFFF", "0123456789ABCDEF", "01");
	if (str) {
		puts(str);
		free(str);
	}
	else {
		perror("base_convert");
		return EXIT_FAILURE;
	}
	str = base_convert("1111111111111111", "01", "0123456789ABCDEF");
	if (str) {
		puts(str);
		free(str);
	}
	else {
		perror("base_convert");
		return EXIT_FAILURE;
	}
	str = base_convert("", "0123456789", "0123456789ABCDEF");
	if (str) {
		puts(str);
		free(str);
	}
	else {
		perror("base_convert");
		return EXIT_FAILURE;
	}
	return EXIT_SUCCESS;
}
